const loadSVG = require('loadSVG')

function Graphic(url, className) {
    let el = document.createElement('div')
    if (className) {
        el.classList.add(className)
    }
    loadSVG(url, (err, svg) => {
        if (err) return console.error(err)
        el.append(svg)
    })

    return el
}   

module.exports = Graphic